import "./App.css";
import React from "react";
// import { useState } from "react";
import Map from "./components/Map";
import axios from "axios";

class App extends React.Component {

  /*const [location, setLocation] = useState('');
  const [temp, setTemp] = useState();
  const position = [51.505, -0.09];*/

  constructor(props) {
    super(props);
    this.state = {
      lat: null,
      long: null,
      location: null,
      temp: null,
      position : [51.505, -0.09],
      zoom: 13,
    };
  }

  handleChange = (event) => {
    // setLocation(event.target.value);
    this.setState({location: event.target.value});
  }

  getLocation = async(term) => {
    axios.get("http://localhost:3001/api/location", {
      params: {
        location: term,
      },
    }).then(response => {
        this.setState({
          lat: response.data.location.latitude[0],
          long: response.data.location.longitude[0],
        });
    }).catch(error => {
        console.error(error);
    })
  };

  onTermSubmit = async(term) => {
    axios.get("http://localhost:3001/api/weather", {
      params: {
        location: term,
      },
    }).then(response => {
        this.setState({
          temp: response.data.max_temp,
        });
        this.getLocation(term);
    }).catch(error => {
        console.log(error);
    })
  };

  changeLocation = () => {
    this.setState({
      lat: 37.369,
      long: 97.36,
    });
  };

  render() {
    return (
      <div>
        {this.state.location}
        {this.state.temp}
        {this.state.long}
        {this.state.lat}
        <div>
          <input type="text" placeholder="Enter" 
            onChange={(event) => this.handleChange(event)} />
          <button className="ui secondary button" 
              onClick={() => {this.onTermSubmit(this.state.location)}}>
              Okay
          </button>
          <button className="ui secondary button"
            onClick={() => {this.changeLocation(this.state.position)}}>
            Okay
          </button>
        </div>
        <Map position={this.state.position} zoom={this.state.zoom}/>
      </div>
    );
  }
}

export default App;
