import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import 'leaflet/dist/leaflet.css';

export default function Map({ position, zoom, temperature }) {

  const markers = positions.map((position, index) => (
    <Marker key={index} position={position} />
  ));

  return (
    <MapContainer center={position[0]} zoom={zoom} scrollWheelZoom={false}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      <Marker position={position[0]}>
        <Popup>
          Temprature is <br /> {temperature}*
        </Popup>
      </Marker>
      <Marker position={position[1]}/>
    </MapContainer>
  )
}
