import "./App.css";
import React from "react";
import Map from "./components/Map";
import axios from "axios";
import { useState } from "react";

function App() {

  const [location, setLocation] = useState('');
  const [lat, setLat] = useState(7.8);
  const [long, setLong] = useState(37.2);
  const [temp, setTemp] = useState(15);
  const [position , setPosition] = useState(
    [
      [12.120000, 76.680000],
      [24.879999, 74.629997],
      [16.994444, 73.300003],
      [19.155001, 72.849998],
      [24.794500, 73.055000],
      [21.250000,	81.629997],
      [16.166700,	74.833298],
      [26.850000,	80.949997],
      [28.679079,	77.069710],
      [19.076090,	72.877426],
      [14.167040,	75.040298],
	    [26.540457,	88.719391],
	    [24.633568,	87.849251],
	    [28.440554,	74.493011],
	    [24.882618,	72.858894],
      [16.779877,	74.556374],
	    [12.715035,	77.281296],
	    [13.432515,	77.727478],
	    [12.651805,	77.208946],
	    [22.728392,	71.637077],
	    [9.383452,	76.574059],
	    [14.623801,	75.621788],
	    [10.925440,	79.838005],
	    [15.852792,	74.498703],
	    [19.354979,	84.986732],
	    [23.905445,	87.524620],
	    [20.296059,	85.824539],
	    [21.105001,	71.771645],
	    [30.172716,	77.299492],
  	  [25.477585,	85.709091],
    ]
  );
  const [zoom, setZoom] = useState(6);

  const addToArray = (newValue) => {
    setPosition([...position, newValue]);
  }

  const handleChange = (event) => {
     setLocation(event.target.value);
  }

  const getLocation = async(term) => {
    axios.get("http://localhost:3001/api/location", {
      params: {
        location: term,
      },
    }).then(response => {
        setLat(response.data.location.latitude[0]);
        setLong(response.data.location.longitude[0]);
        changeLocation();
    }).catch(error => {
        console.error(error);
    })
  };

  const onTermSubmit = async(term) => {
    axios.get("http://localhost:3001/api/weather", {
      params: {
        location: term,
      },
    }).then(response => {
        setTemp(response.data.max_temp);
        getLocation(term);
    }).catch(error => {
        console.log(error);
    })
  };

  const changeLocation = () => {
    setPosition([lat, long]);
  };


    return (
      <div>
        location = {location} 
        temp = {temp} 
        lat = {lat} 
        long = {long} 
        pos = {position[0]}
        <div className="ui container">
          <form onSubmit={() => {onTermSubmit(location)}} className="ui form">
              <div className="field">
                <label>Search for location</label>
                <input className="ui input focus" 
                  type="text" 
                  onChange={(event) => handleChange(event)} />
              </div>
          </form>
          <div className="ui grid">
            <div className="ui row">
              <button className="ui secondary button" 
                  onClick={() => {onTermSubmit(location)}}>
                  Okay
              </button>
              <button className="ui secondary button"
                onClick={() => {changeLocation(position)}}>
                Okay
              </button>
            </div>
            <Map key={position} position={position} zoom={zoom} temperature={temp}/>
          </div>
        </div>
      </div>
    );

}

export default App;
