import "./App.css";
import React from "react";
import Map from "./components/Map";
import axios from "axios";
import { useState } from "react";

function App() {

  const [location, setLocation] = useState('');
  const [lat, setLat] = useState(7.8);
  const [long, setLong] = useState(37.2);
  const [temp, setTemp] = useState(null);
  const [position , setPosition] = useState([51.505, -0.09]);
  const [zoom, setZoom] = useState(13);

  const handleChange = (event) => {
     setLocation(event.target.value);
  }

  const getLocation = async(term) => {
    axios.get("http://localhost:3001/api/location", {
      params: {
        location: term,
      },
    }).then(response => {
        setLat(response.data.location.latitute[0]);
        setLong(response.data.location.longitude[0]);
    }).catch(error => {
        console.error(error);
    })
  };

  const onTermSubmit = async(term) => {
    axios.get("http://localhost:3001/api/weather", {
      params: {
        location: term,
      },
    }).then(response => {
        setTemp(response.data.max_temp);
        getLocation(term);
    }).catch(error => {
        console.log(error);
    })
  };

  const changeLocation = () => {
    setPosition([7.8, 37.2]);
  };


    return (
      <div>
        {location}
        {temp}
        {long}
        {lat}
        {position}
        <div>
          <input type="text" placeholder="Enter" 
            onChange={(event) => handleChange(event)} />
          <button className="ui secondary button" 
              onClick={() => {onTermSubmit(location)}}>
              Okay
          </button>
          <button className="ui secondary button"
            onClick={() => {changeLocation(position)}}>
            Okay
          </button>
        </div>
        <Map key={position} position={position} zoom={zoom}/>
      </div>
    );

}

export default App;
