import "./App.css";
import React from "react";
import Map from "./components/Map";
import axios from "axios";
import { useState } from "react";

function App() {

  const [location, setLocation] = useState('');
  /*const [temp, setTemp] = useState();
  const position = [51.505, -0.09];*/

  const [lat, setLat] = useState(null);
  const [long, setLong] = useState(null);
  const [temp, setTemp] = useState(null);
  const [position , setPosition] = useState([51.505, -0.09]);
  const [zoom, setZoom] = useState(13);

  const handleChange = (event) => {
     setLocation(event.target.value);
  }

  const getLocation = async(term) => {
    axios.get("http://localhost:3001/api/location", {
      params: {
        location: term,
      },
    }).then(response => {
        setLat(response.data.location.latitute[0]);
        setLong(response.data.location.longitude[0]);
    }).catch(error => {
        console.error(error);
    })
  };

  const onTermSubmit = async(term) => {
    axios.get("http://localhost:3001/api/weather", {
      params: {
        location: term,
      },
    }).then(response => {
        this.setState({
          temp: response.data.max_temp,
        });
        this.getLocation(term);
    }).catch(error => {
        console.log(error);
    })
  };

  const changeLocation = () => {
    this.setState({
      position: [97.8, 37.2],
    });
  };


    return (
      <div>
        {this.state.location}
        {this.state.temp}
        {this.state.long}
        {this.state.lat}
        {this.state.position}
        <div>
          <input type="text" placeholder="Enter" 
            onChange={(event) => this.handleChange(event)} />
          <button className="ui secondary button" 
              onClick={() => {this.onTermSubmit(this.state.location)}}>
              Okay
          </button>
          <button className="ui secondary button"
            onClick={() => {this.changeLocation(this.state.position)}}>
            Okay
          </button>
        </div>
        <Map key={this.state.position} position={this.state.position} zoom={this.state.zoom}/>
      </div>
    );

}

export default App;
