import "./App.css";
import React from "react";
// import { useState } from "react";
import Map from "./components/Map";
import axios from "axios";

class App extends React.Component {

  /*const [location, setLocation] = useState('');
  const [temp, setTemp] = useState();
  const position = [51.505, -0.09];*/

  constructor(props) {
    super(props);
    this.state = {
      lat: null,
      long: null,
      location: null,
      temp: null,
      position : [51.505, -0.09],
    };
  }

  handleChange = (event) => {
    // setLocation(event.target.value);
    this.setState({location: event.target.value});
  }

  getLocation = async(term) => {
    axios.get("http://localhost:3001/api/location", {
      params: {
        location: term,
      },
    }).then(response => {
        this.setState({
          lat: response.data.lat[0],
          long: response.data.long[0],
        });
    });
  };

  onTermSubmit = async(term) => {
    axios.get("http://localhost:3001/api/weather", {
      params: {
        location: term,
      },
    }).then(response => {
        this.setState({
          temp: response.data.max_temp,
        });
        this.getLocation(term);
    }).catch(error => {
        console.log(error);
    })
  };
  render() {
    return (
      <div>
        {this.state.location}
        {this.state.temp}
        <div>
          <input type="text" placeholder="Enter" 
            onChange={(event) => this.handleChange(event)} />
          <button className="ui secondary button" 
              onClick={() => {this.onTermSubmit(this.state.location)}}>
              Okay
          </button>
        </div>
        <Map position={this.state.position}/>
      </div>
    );
  }
}

export default App;
